    <div class=\"container mt-5\">
        <h1 class=\"page-title text-center mb-4\">Ajouter un avis pour un lieu de camping</h1>

        {{ form_start(form) }}
        
        <div class=\"form-group\">
            {{ form_label(form.commentaire, 'Votre commentaire') }}
            {{ form_widget(form.commentaire, {'attr': {'class': 'form-control'}}) }}
            {% if form.commentaire.vars.errors|length > 0 %}
                <div class=\"form-error text-danger mt-2\">{{ form.commentaire.vars.errors|first }}</div>
            {% endif %}
        </div>

       

        <button type=\"submit\" class=\"btn btn-primary mt-4\">Envoyer</button>

        {{ form_end(form) }}
    </div>
{% endblock %}
